    """
    Determines which script is being used.
    """    
    def is_kanji(character):
        return 0x4e00 <= ord(character) <= 0x9faf 

    def is_hiragana(character):
        return 0x30a0 <= ord(character) <= 0x30ff

    def is_katakana(character):
        return 0x3040 <= ord(character) <= 0x309f

    def is_joyo(character):
        return joyo_list.get(character) is 0